import React, { Component } from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import './article.css'
import { MDBContainer, MDBInput, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class Article extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            reponse: "",
            comment: [],
            modal: false
        };
    }


    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    componentDidMount() {
        var tab = []
        console.log(this.props)
        axios.get("http://localhost:8080/profil").then(res => {
            console.log('res comment: ', res.data)
            for (let i = 0; i < res.data.length; i++) {
                if (localStorage.getItem('id') == res.data[i].utilisateur) {
                    tab.push(res.data[i])
                }
            }
            this.setState({ comment: tab })
            console.log('comment: ', this.state.comment)
        })
    }

    render() {
        return (
            <div>
                {this.state.comment.length > 0 ? (this.state.comment.sort((a, b) => { return b._id - a._id }).map(article => {
                    console.log('article-image: ', article.image);
                    //const url = "http://localhost:8080/article/" + article.image
                    return (
                        // <div>
                        //     <div class="card">
                        //         <button onClick={this.toggle}><img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} /></button>
                        //         <div class="card-body">
                        //             <center>
                        //                 <h5 class="card-title">{article.titre}</h5>
                        //                 <p class="test">{article.prix}</p>
                        //             </center>
                        //         </div>
                        //     </div>
                        //     <br />
                        // </div>

                        <div>
                            <div class="row">

                                {/* <!--Grid column--> */}
                                <div class="col-md-6 mb-4">

                                    {/* <!-- Card --> */}
                                    <div class="card gradient-card">

                                        <div class="card-image" style="background-image: url(https://mdbootstrap.com/img/Photos/Horizontal/Work/4-col/img%20%2814%29.jpg)">

                                            {/* <!-- Content --> */}
                                            <a href="#!">
                                                <div class="text-white d-flex h-100 mask blue-gradient-rgba">
                                                    <div class="first-content align-self-center p-3">
                                                        <h3 class="card-title">Today's sales</h3>
                                                        <p class="lead mb-0">Click on this card to see details</p>
                                                    </div>
                                                    <div class="second-content align-self-center mx-auto text-center">
                                                        <i class="far fa-money-bill-alt fa-3x"></i>
                                                    </div>
                                                </div>
                                            </a>

                                        </div>

                                        {/* <!-- Data --> */}
                                        <div class="third-content ml-auto mr-4 mb-2">
                                            <p class="text-uppercase text-muted">Today's sale</p>
                                            <h4 class="font-weight-bold float-right">2000$</h4>
                                        </div>

                                        {/* <!-- Content --> */}
                                        <div class="card-body white">
                                            <div class="progress md-progress">
                                                <div class="progress-bar bg-primary" role="progressbar" style="width: 25%;" aria-valuenow="25" aria-valuemin="0" aria-valuemax="100"></div>
                                            </div>
                                            <p class="text-muted">Better than last week (25%)</p>
                                            <h4 class="text-uppercase font-weight-bold my-4">Details</h4>
                                            <p class="text-muted" align="justify">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ipsam vel dolores qui, necessitatibus aut eaque magni mollitia tenetur molestiae sit quae quos quaerat amet exercitationem atque animi odio.</p>
                                        </div>

                                    </div>
                                    {/* <!-- Card --> */}

                                </div>
                                {/* <!--Grid column--> */}

                            </div>

</div>
                            )
                    }
                    )) : ''}
            </div>
                    )
                }
}

                const mapStateToProps = (state) => {
    return {
                    listeArticle: state
        }
    }

export default connect(mapStateToProps)(Article)
