import React, { Component } from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import './article.css'
import { MDBContainer, MDBInput, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class Article extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            reponse: "",
            comment: [],
            modal: false,
        };
    }


    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    componentDidMount() {
        var tab = []
        console.log(this.props)
        axios.get("http://localhost:8080/profil").then(res => {
            console.log('res comment: ', res.data)
            for (let i = 0; i < res.data.length; i++) {
                if (localStorage.getItem('id') == res.data[i].utilisateur) {
                    tab.push(res.data[i])
                }
            }
            this.setState({ comment: tab })
            console.log('comment: ', this.state.comment)
        })
    }

    render() {
        return (
            <div className='container'>
                <div className='row' carte>
                    {this.state.comment.length > 0 ? (this.state.comment.sort((a, b) => { return b._id - a._id }).map(article => {
                        console.log('article-image: ', article.image);
                        return (
                            <div className='col-md-5 carte' key={article._id}>
                                <div className="card">
                                    <button onClick={this.toggle}><img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} /></button>
                                    <div class="card-body">
                                        <center>
                                            <h5 class="card-title">{article.titre}</h5>
                                            <p class="test">{article.prix}</p>
                                        </center>
                                    </div>
                                    <MDBContainer key={article._id}>
                                        <MDBModal isOpen={this.state.modal}>
                                            <MDBModalHeader>{article.titre}</MDBModalHeader>
                                            <MDBModalBody>
                                                <center>
                                                    <img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} />
                                                    <p>{article.description}</p>
                                                    <p>{article.prix}</p>
                                                </center>
                                            </MDBModalBody>
                                            <MDBModalFooter>
                                                <MDBBtn color="secondary" onClick={() => { this.toggle() }}>Close</MDBBtn>
                                            </MDBModalFooter>
                                        </MDBModal>
                                    </MDBContainer>
                                </div>
                                <br />
                            </div>

                        )
                    }
                    )) : ''}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        listeArticle: state
    }
}

export default connect(mapStateToProps)(Article)
