import axios from 'axios'

const initialState = {
  client: [{
    nom: '',
    email: '',
    password: '',
    pwd: ''
  }]
}

function connexion(state = initialState, action) {
  let nextState = [0]
  switch (action.type) {

    case 'REGISTER':
      console.log("action: ", action.value);

     /*  //axios.post('https://shrouded-shore-94366.herokuapp.com/', action.value)
      axios.post('http://localhost:8080/register', {
        nom: action.value.nom,
        email: action.value.email,
        password: action.value.password,
        pwd: action.value.pwd
      })
        .then((response) => {
          nextState = response.data
          //localStorage.setItem('connecte',true)
          console.log("post ok: res.data ", response.data);
          //console.log(response, localStorage.getItem('connecte'));
          return nextState
        })
        .catch((error) => {
          console.log("erreur be: ", error);
        }); */


    case 'GETARTICLE':
      //axios.get('https://shrouded-shore-94366.herokuapp.com/')
      axios.get('http://localhost:8080/')
        .then(function (response) {
          nextState = response.data
          console.log("ttt", nextState);
          return nextState
        })
        .catch(function (error) {

          console.log(error);
        })
      break;

    default:
      return state
  }
}

export default connexion
