import React from 'react';
import { connect } from 'react-redux'

class Article extends React.Component {


    constructor(props) {
        super(props);
        this.state = { 
          reponse: "",
          comment:[]
           };
      }


    callAPI() {
        fetch("http://localhost:8080/profil")
            .then(res => res.json())
            .then(res =>{  
                console.log("res",res);
                /* for(let i=0;i<res.length;i++){
                    if(res[i]._id===this.props.match.params.id){
                        console.log(res[i]);
                        this.setState({ reponse: res[i],
                            comment:res[i].comment
                         })
                        console.log("state ",this.state.comment);
                    }
                } */

               });

    }

    componentDidMount() {
        console.log(this.props.match.params.id);
        this.callAPI();

    }
    render() {
        return (
            <div>
               <div>{this.state.reponse.titre}</div> 
               <div>{this.state.reponse.description}</div>
                 <div>{this.state.comment.length>0?<div>{this.state.comment.map((com)=>{
                     return <div>
                         <div>
                             titre : {com.titre}
                         </div>
                         <div>
                             description : {com.description}
                         </div>
                     </div>
                 })}</div>:""}</div>

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        listeArticle: state.listeArticle
    }
}
export default connect(mapStateToProps)(Article)
