import React, { Component } from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { confirmAlert } from 'react-confirm-alert'; // Import
import './article.css'
import { MDBContainer, MDBInput, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class Article extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            reponse: "",
            comment: [],
            modal: false,
        };
    }


    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    componentDidMount() {
        var tab = []
        console.log('props: ', this.props)
        axios.get("http://localhost:8080/profil").then(res => {
            console.log('res comment: ', res.data)
            for (let i = 0; i < res.data.length; i++) {
                if (localStorage.getItem('id') == res.data[i].utilisateur) {
                    tab.push(res.data[i])
                }
            }
            this.setState({ comment: tab })
            console.log('comment: ', this.state.comment)
        })
    }


    render() {
        const initial = { titre: '', description: '', prix: '' }

        const [user, setUser] = useState(initial)
        const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
        }

        return (
            // AJOUT
            <center onSubmit={event => {
                event.preventDefault()
            }}>
                <table className="table table-bordered">
                    <thead>
                        <tr className="tab">
                            <th>Nom du produit</th>
                            <th>Description</th>
                            <th>Prix</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.comment.length > 0 ? (
                            this.state.comment.sort((a, b) => { return b._id - a._id }).map(user => (
                                <tr key={user._id}>
                                    <td id="titre" onChange={changed}>{user.titre}</td>
                                    <td id="description" onChange={changed}>{user.description}</td>
                                    <td id="prix" onChange={changed}>{user.prix}</td>
                                    <td>
                                        {/* SUPPRESSION */}
                                        <button className="btn btn-danger"
                                            onClick={() => {
                                                confirmAlert({
                                                    customUI: ({ onClose }) => {
                                                        return (
                                                            <center>
                                                                <div className="custom-ui" id="popup">
                                                                    <h6>Suppression Produit</h6><br />
                                                                    <h6>{user.titre}</h6><br />
                                                                    <button className="btn btn-dark"
                                                                        onClick={() => {
                                                                            // props.deleteUser(user.id); //Appel de la fonction deleteUser App.js
                                                                            onClose();
                                                                        }}
                                                                    >
                                                                        OUI
                                                            </button><a>&nbsp;&nbsp;</a>
                                                                    <button className="btn btn-dark" onClick={onClose}>NON</button>
                                                                </div>
                                                            </center>
                                                        );
                                                    }
                                                })
                                            }
                                            }
                                        >X</button>
                                        <a>&nbsp;</a>
                                        {/* MODIFICATION */}
                                        <button className="btn btn-success"
                                            onClick={() => {
                                                confirmAlert({
                                                    customUI: ({ onClose }) => {
                                                        return (
                                                            <form id='ID_FORMULAIRE'>
                                                                <center>
                                                                    <div className="custom-ui" id="popup">
                                                                        <input name='inputStoreID' placeholder={user.prix} id="entree" className="modif"></input><br />
                                                                        <p id="e"></p><br />

                                                                        <button className="btn btn-dark"
                                                                            onClick={(e) => {
                                                                                user.prix = document.forms['ID_FORMULAIRE'].elements['inputStoreID'].value //Affectation du contenu de l'input dans user.prix 
                                                                                if (isNaN(user.prix) || user.prix == "") {
                                                                                    var valid = "Entrer un Nombre"
                                                                                    e.preventDefault()
                                                                                    document.getElementById('e').innerHTML = valid; //Affichage de la variable valid dans le paragraphe e
                                                                                } else {
                                                                                    valid = "";
                                                                                    document.getElementById('e').innerHTML = valid;
                                                                                    // props.updateUser(user.prix, user) //Appel de la fonction updateUser App.js
                                                                                    // props.editRow(user.id); //Appel de la fonction editRow App.js
                                                                                    onClose();
                                                                                }
                                                                            }
                                                                            }
                                                                        >OK</button><a>&nbsp;&nbsp;</a>

                                                                        <button className="btn btn-dark" onClick={onClose}>Annuler</button>
                                                                    </div>
                                                                </center>
                                                            </form>
                                                        );
                                                    }
                                                })
                                            }
                                            }
                                        >Edit</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                                <tr>
                                    <td colSpan={4}></td>
                                </tr>
                            )}
                    </tbody>
                </table>
            </center>





            // <div className='container'>
            //     <div className='row' card>
            //         {this.state.comment.length > 0 ? (this.state.comment.sort((a, b) => { return b._id - a._id }).map(article => {
            //             console.log('article-image: ', article._id);
            //             return (
            //                 <div className='col-md-5 carte' key={this.state.comment._id}>
            //                     <div className="card">
            //                         <button onClick={this.toggle}><img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} /></button>
            //                         <div class="card-body">
            //                             <center>
            //                                 <h5 class="card-title">{article.titre}</h5>
            //                                 <p class="test">{article.prix}</p>
            //                             </center>
            //                         </div>
            //                         <MDBContainer>
            //                             <MDBModal isOpen={this.state.modal}>
            //                                 <MDBModalHeader>{this.props.titre}</MDBModalHeader>
            //                                 <MDBModalBody>
            //                                     <center>
            //                                         <img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} />
            //                                         <p>{article.description}</p>
            //                                         <p>{article.prix}</p>
            //                                     </center>
            //                                 </MDBModalBody>
            //                                 <MDBModalFooter>
            //                                     <MDBBtn color="secondary" onClick={() => { this.toggle() }}>Close</MDBBtn>
            //                                 </MDBModalFooter>
            //                             </MDBModal>
            //                         </MDBContainer>
            //                     </div>
            //                     <br />
            //                 </div>

            //             )
            //         }
            //         )) : ''}
            //     </div>
            // </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        listeArticle: state
    }
}

export default connect(mapStateToProps)(Article)
