import React from 'react';
import { MDBInput, MDBBtn } from "mdbreact";

class PostFrontToBack extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            titre: '',
            description: '',
            prix: '',
            image: ''

        };
        this.handleChange = this.handleChange.bind(this)
        this.handleUploadImage = this.handleUploadImage.bind(this);
    }




    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleUploadImage(ev) {
        ev.preventDefault();

        const data = new FormData();
        data.append('image', this.uploadInput.files[0]);
        data.append('titre', this.state.titre);
        data.append('description', this.state.description);
        data.append('prix', this.state.prix)

        fetch('http://localhost:8080/profil', {
            method: 'POST',
            body: data,
        }).then((response) => {
            response.json().then((body) => {
                this.setState({ image: `http://localhost:8080/profil/${body.image}` });
                console.log('ity ilay body.fil', body.image);
            });
        });
    }

    render() {
        return (

            <div> {/* //DEBUT */}
                <div class="card">

                    <h5 class="card-header info-color white-text text-center">
                        <strong>NOUVEAU PRODUIT</strong>
                    </h5>

                    <div class="card-body">

                        <form class="text">

                            <MDBInput label="Nom du Produit" icon="user" id="un" type="text" className="input black-text" name="titre" value={this.state.value} onChange={this.handleChange} />
                            <MDBInput label="Descriptions" size="lg" icon="at" id="ml" type="textarea" className="input black-text" name="description" value={this.state.value} onChange={this.handleChange} />
                            <MDBInput label="Prix" size="lg" icon="lock" id="pw" type="number" className="input black-text" name="prix" value={this.state.value} onChange={this.handleChange} />
                            <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image" />
                            <MDBBtn class="btn btn-outline-info btn-rounded btn-block my-4 waves-effect z-depth-0" type="submit" onClick={this.handleUploadImage}>Publier</MDBBtn>

                        </form>

                    </div>

                </div>
            </div> /* //FIN */


            // {/* // <form onSubmit={this.handleUploadImage}> ok
