import React from 'react';
import './register.css'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import axios from 'axios'

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = { nom: '', email: '', password: '', pwd: '', connecte: false }
        this.handleChange = this.handleChange.bind(this)
        this.enregistrement = this.enregistrement.bind(this)
    }
    renderRedirect = () => {
        if (this.state.connecte) {
            console.log(localStorage.getItem('connecte'));

            return <Redirect to='/dashboard' />
        }
    }

 /*    test = () => {
        this.setState({connecte: true})
        axios.post("http://localhost:8080/register", {
            nom: this.state.nom,
            email: this.state.email,
            password: this.state.password,
            pwd: this.state.pwd
        })
            .then(res => {
                this.setState({nom: res.data.nom})
                //res.send()
                console.log("post ok: res.data ", res.data);
            })
            .catch(errr => {
                console.log("connect: ", this.state.connecte);
                console.log("state: ", this.state);
                console.log("erreur post: ", errr);

            })
    } */

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

     enregistrement(event) {
          const action = { type: "REGISTER", value: event }
          this.props.dispatch(action)
      }

    render() {
        return (
            <div id="totalregister">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-3"></div>
                        <div className="col-md-6">
                            <div className="login-wrap">
                                <div className="login-html">
                                    <div className="login-form">
                                        <div className="sign-in-htm">
                                            {/* formulaire inscription  */}
                                            <div className="sign-up-htm">
                                                <center>
                                                    <img id="image-login" src="../images/logo.png" alt="logoimage" />
                                                </center>
                                                {this.renderRedirect()}
                                                <form onSubmit={this.enregistrement}>
                                                    <div className="group">
                                                        <label for="user" className="label">Nom d'utilisateur</label>
                                                        <input id="un" type="text" className="input" name="nom" value={this.state.value} placeholder="nom d'utilisateur" onChange={this.handleChange} />
                                                    </div>
                                                    <div className="group">
                                                        <label for="user" className="label">E-mail:</label>
                                                        <input id="ml" type="email" className="input" name="email" value={this.state.value} placeholder="exemple@exemple.com" onChange={this.handleChange} />
                                                    </div>

                                                    <div className="group">
                                                        <label for="user" className="label">Mot de passe:</label>
                                                        <input id="pw" type="passWord" className="input" name="password" value={this.state.value} placeholder="********" onChange={this.handleChange} />
                                                    </div>

                                                    <div className="group">
                                                        <label for="pass" className="label">Confirmer mot de passe:</label>
                                                        <input id="" type="passWord" className="input" data-type="passWord" name="pwd" value={this.state.value} placeholder="********" onChange={this.handleChange} />
                                                    </div>
                                                    <p id='pass'></p>

                                                    <div className="group">
                                                        <button className="button" id="boutton">S'inscrire</button>
                                                    </div>
                                                </form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3"></div>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        listeArticle: state.listeArticle
    }
}
export default connect(mapStateToProps)(Register)
