import React, { Component } from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import './article.css'
import { MDBContainer, MDBInput, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class Article extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            reponse: "",
            comment: []
        };
    }

    componentDidMount() {
        var tab = []
        console.log(this.props)
        axios.get("http://localhost:8080/profil").then(res => {
            console.log('res comment: ', res.data)
            for(let i=0; i<res.data.length; i++){
                if (localStorage.getItem('id')==res.data[i].utilisateur){
                    tab.push(res.data[i])
                }
            }
            this.setState({ comment: tab })
            console.log('comment: ', this.state.comment)
        })
    }

    render() {
        return (
            <div>
                {this.state.comment.length > 0 ? (this.state.comment.sort((a,b) => {return b._id - a._id}).map(article => {
                    console.log('article-image: ', article.image);
                    //const url = "http://localhost:8080/article/" + article.image
                    return (
                        <div>
                            <div class="card">
                                <button><img class="card-img-top img-thumbnail" src={"http://localhost:8080/profil/" + article.image} alt={article.titre} /></button>
                                <div class="card-body">
                                    <center>
                                    <h5 class="card-title">{article.titre}</h5>
                                    <p class="test">{article.prix}</p>
                                    </center>
                                </div>
                            </div>
                            <br/>



                        <MDBContainer>
                        <MDBBtn onClick={this.toggle}>Ajouter</MDBBtn>
                        <MDBModal isOpen={this.state.modal}>
                            <MDBModalHeader>Enregistrement...</MDBModalHeader>
                            <MDBModalBody><center>Ajout du Produit avec succÃ©s</center></MDBModalBody>
                            <MDBModalFooter>
                                <MDBBtn color="secondary" onClick={()=>{this.renderRedirect()}}>Close</MDBBtn>
                            </MDBModalFooter>
                        </MDBModal>
                    </MDBContainer>




                    </div>
                    )
                }
                )) : ''}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        listeArticle: state
    }
}

export default connect(mapStateToProps)(Article)
