import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'

class Article extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            reponse: "",
            comment: []
        };
    }


    /*    callAPI() {
           fetch("http://localhost:8080/profil")
               .then(res => res.json())
               .then(res =>{  
                   console.log("res",res);
                   this.setState({comment: res})
                   console.log('comment: ', this.state.comment);

              for(let i=0;i<res.length;i++){
                       if(res[i]._id===this.props.match.params.id){
                           console.log(res[i]);
                           this.setState({ reponse: res[i],
                               comment:res[i].comment
                            })
                           console.log("state ",this.state.comment);
                       }
                   }

                  });

       }
   */

    componentDidMount() {        
        console.log(this.props)
        axios.get("http://localhost:8080/profil").then(res => {
            console.log('res comment: ',res.data)
            this.setState({ comment: res.data })
            console.log('comment: ',this.state.comment.image)
        })
    }

    render() {
        return (
            <div>
                {this.state.comment.length > 0 ? (this.state.comment.map(article => {
                    const url = "http://localhost:8080/article/" + article.image
                    return (
                        <div class="card">
                            <img class="card-img-top" src={url} alt="Card image cap" />
                            <div class="card-body">
                                <h5 class="card-title">{article.nom}</h5>
                                <p class="card-text">{article.description}</p>
                                <a href="#" class="btn btn-primary">{article.prix}</a>
                            </div>
                        </div>
                    )
                }
                )) : ''}
            </div>

            // render() {
            //     return (
            //         <div>
            //            <div>{this.state.comment.titre}</div> 
            //            {<div>{this.state.comment.description}</div>}
            //            <div><img src={this.state.comment.image} alt=""/></div>
            //              <div>{this.state.comment.length>0?<div>{this.state.comment.map((com)=>{
            //                  return <div>
            //                      <div>
            //                          titre : {com.titre}
            //                      </div>
            //                      <div>
            //                          description : {com.description}
            //                      </div>
            //                      <div><img src={com.image} alt=""/></div>
            //                  </div>
            //              })}</div>:""}</div>

            //         </div>
            //     )
            // }
        )}
    }

    const mapStateToProps = (state) => {
        return {
            listeArticle: state.listeArticle
        }
    }

export default connect(mapStateToProps)(Article)
