import React from 'react';

class PostFrontToBack extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            titre: '',
            description: '',
            prix: '',
            image: ''

        };
        this.onChange = this.onChange.bind(this)
        this.handleUploadImage = this.handleUploadImage.bind(this);
    }




    onChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleUploadImage(ev) {
        ev.preventDefault();

        const data = new FormData();
        data.append('image', this.uploadInput.files[0]);
        data.append('titre', this.state.titre);
        data.append('description', this.state.description);
        data.append('prix', this.state.prix)

        fetch('http://localhost:8080/profil', {
            method: 'POST',
            body: data,
        }).then((response) => {
            response.json().then((body) => {
                this.setState({ image: `http://localhost:8080/profil/${body.image}` });
                console.log('ity ilay body.fil', body.image);
            });
        });
    }

    render() {
        return (

            <div> {/* //DEBUT */}
                <div class="card">

                    <h5 class="card-header info-color white-text text-center py-4">
                        <strong>NOUVEAU PRODUIT</strong>
                    </h5>

                    <div class="card-body px-lg-5 pt-0">

                        <form class="text-center" onSubmit={this.handleUploadImage}>

                            <div class="md-form">
                                <input type="text" id="titre" class="form-control" value={this.state.value} onChange={this.onChange} name="titre" />
                                <label for="titre">Nom du Produit</label>
                            </div>

                            <div class="md-form">
                                <input type="text" id="description" class="form-control" value={this.state.value} onChange={this.onChange} name="description" />
                                <label for="description">Descriptions</label>
                            </div>

                            <div class="d-flex justify-content-around">
                                <div>
                                    <a href="">Forgot password?</a>
                                </div>
                            </div>
                            {/* <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image" /> */}
                            <button class="btn btn-outline-info btn-rounded btn-block my-4 waves-effect z-depth-0" type="submit">Publier</button>

                        </form>

                    </div>

                </div>
            </div> /* //FIN */


            // {/* // <form onSubmit={this.handleUploadImage}> ok
            // //     <label>Titre:</label>
            // //     <input type="text"
            // //         value={this.state.value}
            // //         onChange={this.onChange}
            // //         name="titre" /><br></br>
            // //     <label>Description:</label>
            // //     <input type="text"
            // //         value={this.state.value}
            // //         onChange={this.onChange}
            // //         name="description" /><br></br>
            // //     <label>Prix:</label>
            // //     <input type="number"
            // //         value={this.state.value}
            // //         onChange={this.onChange}
            // //         name="prix" /><br></br>

            // //     <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image" />

            // //     <button>Ajouter</button>
            // // </form> */}
        );
    }
}

export default PostFrontToBack;
